import React, { useState } from 'react';
import './App.css';

import axios from 'axios';
import { API_URL } from "./API.js"

function App() {

  const [text, settext] = useState("");
  const [todos, setTodos] = useState<Todo[]>([]);

  type Todo = {
    id: number;
    text: string;
    completed: boolean;
  };

  // const getTodos = () => {
  //   axios.get(API_URL + "todo_list/").then(res => setTodos({ todos: res.data }));
  // };

  // put axios settings

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    settext(e.target.value)
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    // todo  作成
    const newTodo: Todo = {
      id: todos.length,
      text: text,
      completed: false,
    };


    axios.post(API_URL + "create/", newTodo)
      .then(() => {
        console.log(API_URL + "create/");
        console.log(newTodo);
      })

    setTodos([newTodo, ...todos]);
    settext("");
  };

  const handleEdit = (id: number, text: string) => {

    const newTodos = todos.map((todo) => {
      if (todo.id === id) {
        todo.text = text;
      }
      return todo;
    });

    setTodos(newTodos);
  };

  const handleCheckd = (id: number, completed: boolean) => {

    const newTodos = todos.map((todo) => {
      if (todo.id === id) {
        todo.completed = !completed;
      }
      return todo;
    });

    setTodos(newTodos);

  };

  const handleDelete = (id: number) => {
    const newTodos = todos.filter((todo) => todo.id !== id);
    setTodos(newTodos);
  };

  // axios set end

  return (
    <div className="App">
      <h2>Todolist with TypeScript</h2>
      <form action="" onSubmit={(e) => { handleSubmit(e) }}>
        <input type="text" onChange={(e) => { handleChange(e) }} className="inputText" value={text} />
        <input type="submit" value="作成" className="submitButton" />
      </form>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <input type="text"
              onChange={(e) => { handleEdit(todo.id, e.target.value) }}
              className="EditText"
              value={todo.text}
              disabled={todo.completed} />
            <input type="checkbox"
              onChange={(e) => { handleCheckd(todo.id, todo.completed) }}
              className="EditText"
              value={todo.text} />
            <button onClick={(e) => handleDelete(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
