import React, { useState } from 'react';
import './App.css';

import axios from 'axios';
import { API_URL } from "./API.js"

function App() {

    const [inputValue, setInputValue] = useState("");
    const [todos, setTodos] = useState<Todo[]>([]);

    type Todo = {
      inputValue: string;
      id: number;
      checked: boolean;
    };

    // put axios settings

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      setInputValue(e.target.value)
    };

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
      e.preventDefault();

      // todo  作成
      const newTodo: Todo = {
        inputValue: inputValue,
        id: todos.length,
        checked: false,
      };

      axios.get(API_URL).then(() => {
        console.log(API_URL + "update/" + newTodo.id);
        console.log(newTodo);
      })

      setTodos([newTodo, ...todos]);
      setInputValue("");
    };

    const handleEdit = (id: number, inputValue: string) => {

      const newTodos = todos.map((todo) => {
        if (todo.id === id) {
          todo.inputValue = inputValue;
        }
        return todo;
      });

      setTodos(newTodos);
    };

    const handleCheckd = (id: number, checked: boolean) => {

      const newTodos = todos.map((todo) => {
        if (todo.id === id) {
          todo.checked = !checked;
        }
        return todo;
      });

      setTodos(newTodos);

    };

    const handleDelete = (id: number) => {
      const newTodos = todos.filter((todo) => todo.id !== id);
      setTodos(newTodos);
    };

    // axios set end

    return (
      <div className="App">
        <h2>Todolist with TypeScript</h2>
        <form action="" onSubmit={(e) => { handleSubmit(e) }}>
          <input type="text" onChange={(e) => { handleChange(e) }} className="inputText" value={inputValue} />
          <input type="submit" value="作成" className="submitButton" />
        </form>
        <ul>
          {todos.map((todo) => (
            <li key={todo.id}>
              <input type="text"
                onChange={(e) => { handleEdit(todo.id, e.target.value) }}
                className="EditText"
                value={todo.inputValue}
                disabled={todo.checked} />
              <input type="checkbox"
                onChange={(e) => { handleCheckd(todo.id, todo.checked) }}
                className="EditText"
                value={todo.inputValue} />
              <button onClick={(e) => handleDelete(todo.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    );
  }

export default App;
